from sympy import mod_inverse
from gmpy2 import iroot

def chinese_remainder_theorem(c, n):
    N = 1
    for ni in n:
        N *= ni  

    result = 0
    for ci, ni in zip(c, n):
        Ni = N // ni 
        Mi = mod_inverse(Ni, ni)  
        result += ci * Ni * Mi  

    return result % N  

